mapper.delete:
delete from BOOKMARK
where mem_Id = #{mem_Id}
and i_Num = #{i_Num}

mapper.update:
update co_work
set  c_Name = #{c_Name}
   , c_Comment = #{c_Comment}
   , mem_Id = #{mem_Id}
   , c_Category = #{c_Category}
where c_Id = #{c_Id}

mapper.select:

select count(*) from BOOKMARK
where mem_Id = #{mem_Id}
and i_Num = #{i_Num}

mapper.update:

 UPDATE APPLY_MEM
SET AP_YN = 'yes'
where c_Id = #{c_Id}
and mem_Id = #{mem_Id}


DELETE FROM APPLY_MEM
where c_Id = #{c_Id}
and mem_Id = #{mem_Id}
